import axios from 'axios'

export function setUpJWT() {
    localStorage.setItem('Jwt', res.data);
    axios.defaults.headers.common["authorization"] = token;
}

export function handleSignOut() {
    localStorage.removeItem('Jwt');
    props.history.push('/');
}
